      &lt;form @submit.prevent="handleSubmit" class="form"&gt;
        &lt;AppInput
          v-model="form.title"
          label="Título"
          placeholder="Ex: Estudar Vue.js"
          required
        /&gt;

        &lt;AppInput
          v-model.number="form.duration"
          label="Duração (minutos)"
          type="number"
          placeholder="Ex: 60"
          required
        /&gt;

        &lt;div class="textarea-group"&gt;
          &lt;label class="label"&gt;Observações&lt;/label&gt;
