</code></pre> ./src/views/RecordDetailView.vue<pre><code>&lt;script setup&gt;
import { ref, onMounted } from 'vue';
import { useRouter, useRoute } from 'vue-router';
import AppHeader from '@/components/layout/AppHeader.vue';
import AppButton from '@/components/forms/AppButton.vue';
import { useRecords } from '@/composables/useRecords';

const router = useRouter();
const route = useRoute();
const { getRecord, deleteRecord } = useRecords();

const record = ref(null);

onMounted(() =&gt; {
  record.value = getRecord(route.params.id);
  if (!record.value) {
    router.push('/records');
  }
});

function formatDate(isoDate) {
  return new Date(isoDate).toLocaleDateString('pt-BR', {
    day: '2-digit',
    month: 'long',
    year: 'numeric',
  });
}

function handleEdit() {
  router.push(`/records/${route.params.id}/edit`);
}

function handleDelete() {
  if (confirm('Deseja realmente excluir este registro?')) {
    deleteRecord(route.params.id);
    router.push('/records');
  }
}
&lt;/script&gt;

&lt;template&gt;
  &lt;div v-if="record"&gt;
    &lt;AppHeader title="Detalhes" show-back @back="router.back()" /&gt;

    &lt;div class="page"&gt;
      &lt;div class="card"&gt;
        &lt;h1 class="title"&gt;{{ record.title }}&lt;/h1&gt;

        &lt;div class="info"&gt;
          &lt;div class="info-item"&gt;
            &lt;span class="label"&gt;⏱️ Duração:&lt;/span&gt;
            &lt;span class="value"&gt;{{ record.duration }} minutos&lt;/span&gt;
          &lt;/div&gt;
          &lt;div class="info-item"&gt;
