./src/views/RecordFormView.vue<pre><code>&lt;script setup&gt;
import { ref, computed, onMounted } from 'vue';
import { useRouter, useRoute } from 'vue-router';
import AppHeader from '@/components/layout/AppHeader.vue';
import AppInput from '@/components/forms/AppInput.vue';
import AppButton from '@/components/forms/AppButton.vue';
import { useRecords } from '@/composables/useRecords';

const router = useRouter();
const route = useRoute();
const { addRecord, getRecord, updateRecord } = useRecords();

const isEditMode = computed(() =&gt; route.params.id !== 'new');

const form = ref({
  title: '',
  duration: '',
  notes: '',
});

onMounted(() =&gt; {
  if (isEditMode.value) {
    const record = getRecord(route.params.id);
    if (record) {
      form.value = {
        title: record.title,
        duration: record.duration,
        notes: record.notes || '',
      };
    } else {
      router.push('/records');
    }
  }
});

function handleSubmit() {
  if (!form.value.title || !form.value.duration) {
    alert('Preencha os campos obrigatórios');
    return;
  }

  if (isEditMode.value) {
    updateRecord(route.params.id, form.value);
  } else {
    addRecord(form.value);
  }

  router.push('/records');
}
&lt;/script&gt;

