      @back="router.back()"
    /&gt;

    &lt;div class="page"&gt;
      &lt;form @submit.prevent="handleSubmit" class="form"&gt;
        &lt;AppInput
          v-model="form.title"
          label="Título"
          placeholder="Ex: Estudar Vue.js"
          required
        /&gt;

        &lt;AppInput
          v-model.number="form.duration"
