            rows="4"
            class="textarea"
            placeholder="Adicione observações sobre a atividade..."
          &gt;&lt;/textarea&gt;
        &lt;/div&gt;

        &lt;AppButton type="submit"&gt;
          {{ isEditMode ? 'Salvar alterações' : 'Criar registro' }}
        &lt;/AppButton&gt;
      &lt;/form&gt;
    &lt;/div&gt;
  &lt;/div&gt;
&lt;/template&gt;

